function tabsContent(){let r=document.querySelectorAll(".player__tabs-btn"),a=document.querySelectorAll(".tabs-content");r.forEach(function(e){e.addEventListener("click",function(t){const c=t.currentTarget.dataset.path;r.forEach(function(e){e.classList.remove("player__tabs-btn--active"),t.currentTarget.classList.add("player__tabs-btn--active"),a.forEach(function(e){e.classList.remove("tabs-content--active"),document.querySelector(`[data-target='${c}']`).classList.add("tabs-content--active")})})})})}tabsContent();const burger=document.querySelector(".burger"),mobileMenu=document.querySelector(".menu"),bodyLock=document.querySelector("body");burger.addEventListener("click",()=>{mobileMenu.classList.toggle("menu--active"),mobileMenu.classList.contains("menu--active")?(burger.classList.add("burger--active"),bodyLock.classList.add("lock")):(burger.classList.remove("burger--active"),bodyLock.classList.remove("lock"))}),document.addEventListener("click",function(e){e.target!==burger&&e.target!==mobileMenu&&(burger.classList.remove("burger--active"),mobileMenu.classList.remove("menu--active"),bodyLock.classList.remove("lock"))});