function tabsContent(){let n=document.querySelectorAll(".player__tabs-btn"),c=document.querySelectorAll(".tabs-content");n.forEach(function(t){t.addEventListener("click",function(e){const a=e.currentTarget.dataset.path;n.forEach(function(t){t.classList.remove("player__tabs-btn--active"),e.currentTarget.classList.add("player__tabs-btn--active"),c.forEach(function(t){t.classList.remove("tabs-content--active"),document.querySelector(`[data-target='${a}']`).classList.add("tabs-content--active")})})})})}tabsContent();